import React, { useState, useMemo } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { 
  CssBaseline, 
  Box, 
  IconButton, 
  Typography, 
  Container,
  Grid,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import AreaCard from './components/AreaCard';

function App() {
  const [mode, setMode] = useState('light');
  const [areas, setAreas] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [newAreaName, setNewAreaName] = useState('');

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode,
        },
      }),
    [mode],
  );

  const toggleColorMode = () => {
    setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
  };

  const handleAddArea = () => {
    if (newAreaName.trim()) {
      setAreas([
        ...areas,
        {
          id: Date.now(),
          name: newAreaName,
          goals: [],
          todos: [],
        },
      ]);
      setNewAreaName('');
      setOpenDialog(false);
    }
  };

  const handleUpdateArea = (updatedArea) => {
    setAreas(areas.map(area => 
      area.id === updatedArea.id ? updatedArea : area
    ));
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: '100vh',
          bgcolor: 'background.default',
          color: 'text.primary',
          py: 4,
        }}
      >
        <Container>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
            <Typography variant="h4" component="h1">
              Life Book App
            </Typography>
            <IconButton onClick={toggleColorMode} color="inherit">
              {mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
            </IconButton>
          </Box>

          <Box sx={{ mb: 4 }}>
            <Button 
              variant="contained" 
              onClick={() => setOpenDialog(true)}
              sx={{ mb: 2 }}
            >
              Add New Area of Life
            </Button>
          </Box>

          <Grid container spacing={3}>
            {areas.map((area) => (
              <Grid item xs={12} md={6} lg={4} key={area.id}>
                <AreaCard area={area} onUpdate={handleUpdateArea} />
              </Grid>
            ))}
          </Grid>
        </Container>

        <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
          <DialogTitle>Add New Area of Life</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              label="Area Name"
              fullWidth
              value={newAreaName}
              onChange={(e) => setNewAreaName(e.target.value)}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
            <Button onClick={handleAddArea} color="primary">
              Add Area
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </ThemeProvider>
  );
}

export default App;
