import React, { useState } from 'react';
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Box,
  Grid,
  LinearProgress,
} from '@mui/material';
import { format } from 'date-fns';

const AreaCard = ({ area, onUpdate }) => {
  const [openDialog, setOpenDialog] = useState(false);
  const [newGoal, setNewGoal] = useState('');
  const [newTodo, setNewTodo] = useState({ title: '', dueDate: '' });

  const handleAddGoal = () => {
    if (newGoal.trim()) {
      const updatedArea = {
        ...area,
        goals: [...area.goals, { id: Date.now(), text: newGoal, completed: false }],
      };
      onUpdate(updatedArea);
      setNewGoal('');
      setOpenDialog(false);
    }
  };

  const handleAddTodo = () => {
    if (newTodo.title.trim() && newTodo.dueDate) {
      const updatedArea = {
        ...area,
        todos: [...area.todos, { 
          id: Date.now(), 
          title: newTodo.title, 
          dueDate: newTodo.dueDate,
          completed: false 
        }],
      };
      onUpdate(updatedArea);
      setNewTodo({ title: '', dueDate: '' });
      setOpenDialog(false);
    }
  };

  const calculateProgress = () => {
    const totalItems = area.goals.length + area.todos.length;
    if (totalItems === 0) return 0;
    const completedItems = 
      area.goals.filter(g => g.completed).length + 
      area.todos.filter(t => t.completed).length;
    return (completedItems / totalItems) * 100;
  };

  return (
    <Card sx={{ minWidth: 275, m: 2 }}>
      <CardContent>
        <Typography variant="h5" component="div" gutterBottom>
          {area.name}
        </Typography>
        <Box sx={{ mb: 2 }}>
          <Typography variant="body2" color="text.secondary">
            Progress
          </Typography>
          <LinearProgress 
            variant="determinate" 
            value={calculateProgress()} 
            sx={{ height: 10, borderRadius: 5 }}
          />
        </Box>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <Typography variant="h6">Goals</Typography>
            {area.goals.map(goal => (
              <Typography key={goal.id} variant="body2">
                • {goal.text}
              </Typography>
            ))}
          </Grid>
          <Grid item xs={6}>
            <Typography variant="h6">To-dos</Typography>
            {area.todos.map(todo => (
              <Typography key={todo.id} variant="body2">
                • {todo.title} (Due: {format(new Date(todo.dueDate), 'MMM dd')})
              </Typography>
            ))}
          </Grid>
        </Grid>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={() => setOpenDialog(true)}>
          Add Item
        </Button>
      </CardActions>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Add New Item</DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle1">Add Goal</Typography>
            <TextField
              fullWidth
              value={newGoal}
              onChange={(e) => setNewGoal(e.target.value)}
              placeholder="Enter new goal"
              sx={{ mb: 2 }}
            />
            <Typography variant="subtitle1">Add To-do</Typography>
            <TextField
              fullWidth
              value={newTodo.title}
              onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
              placeholder="Enter to-do title"
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              type="date"
              value={newTodo.dueDate}
              onChange={(e) => setNewTodo({ ...newTodo, dueDate: e.target.value })}
              InputLabelProps={{ shrink: true }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button onClick={handleAddGoal} color="primary">
            Add Goal
          </Button>
          <Button onClick={handleAddTodo} color="primary">
            Add To-do
          </Button>
        </DialogActions>
      </Dialog>
    </Card>
  );
};

export default AreaCard;
